import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertSessionSchema, insertDrinkSchema } from '@shared/schema';
import { z } from 'zod';

// Validator for UUID
const uuidSchema = z.string().uuid();

export async function registerRoutes(app: Express): Promise<Server> {
  // GET health check endpoint
  app.get('/api/health', (req, res) => {
    res.json({ status: 'ok', message: 'TipsyCalculator server is running' });
  });

  // ======== Session Routes ========

  // Create a new drinking session
  app.post('/api/sessions', async (req, res) => {
    try {
      console.log("Session creation request body:", req.body);
      const sessionData = insertSessionSchema.parse(req.body);
      console.log("Parsed session data:", sessionData);
      const newSession = await storage.createSession(sessionData);
      res.status(201).json(newSession);
    } catch (error) {
      console.error("Session creation error:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: 'Failed to create session' });
    }
  });

  // Get a specific session
  app.get('/api/sessions/:sessionId', async (req, res) => {
    try {
      const { sessionId } = req.params;
      const validId = uuidSchema.parse(sessionId);
      const session = await storage.getSession(validId);

      if (!session) {
        return res.status(404).json({ error: 'Session not found' });
      }

      res.json(session);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: 'Invalid session ID' });
      }
      res.status(500).json({ error: 'Failed to retrieve session' });
    }
  });

  // Update session points
  app.put('/api/sessions/:sessionId', async (req, res) => {
    try {
      const { sessionId } = req.params;
      const validId = uuidSchema.parse(sessionId);
      const { totalPoints, hasMixedDrinks } = req.body;

      if (typeof totalPoints !== 'number' || typeof hasMixedDrinks !== 'boolean') {
        return res.status(400).json({ error: 'Invalid request data' });
      }

      const updatedSession = await storage.updateSessionPoints(validId, totalPoints, hasMixedDrinks);

      if (!updatedSession) {
        return res.status(404).json({ error: 'Session not found' });
      }

      res.json(updatedSession);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: 'Invalid session ID' });
      }
      res.status(500).json({ error: 'Failed to update session' });
    }
  });

  // Get sessions for a user
  app.get('/api/users/:userId/sessions', async (req, res) => {
    try {
      const userId = parseInt(req.params.userId);

      if (isNaN(userId)) {
        return res.status(400).json({ error: 'Invalid user ID' });
      }

      const sessions = await storage.getUserSessions(userId);
      res.json(sessions);
    } catch (error) {
      res.status(500).json({ error: 'Failed to retrieve sessions' });
    }
  });

  // ======== Drink Routes ========

  // Add drink to a session
  app.post('/api/drinks', async (req, res) => {
    try {
      const drinkData = insertDrinkSchema.parse(req.body);
      const newDrink = await storage.addDrink(drinkData);
      res.status(201).json(newDrink);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: 'Failed to add drink' });
    }
  });

  // Get all drinks for a session
  app.get('/api/sessions/:sessionId/drinks', async (req, res) => {
    try {
      const { sessionId } = req.params;
      const validId = uuidSchema.parse(sessionId);
      const drinks = await storage.getSessionDrinks(validId);
      res.json(drinks);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: 'Invalid session ID' });
      }
      res.status(500).json({ error: 'Failed to retrieve drinks' });
    }
  });

  // Remove a drink
  app.delete('/api/drinks/:drinkId', async (req, res) => {
    try {
      const drinkId = parseInt(req.params.drinkId);

      if (isNaN(drinkId)) {
        return res.status(400).json({ error: 'Invalid drink ID' });
      }

      const success = await storage.removeDrink(drinkId);

      if (!success) {
        return res.status(404).json({ error: 'Drink not found' });
      }

      res.status(204).end();
    } catch (error) {
      res.status(500).json({ error: 'Failed to remove drink' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, type User, type InsertUser,
  sessions, type Session, type InsertSession,
  drinks, type Drink, type InsertDrink,
  type FrontendDrink
} from "@shared/schema";
import { eq, and } from "drizzle-orm";
import { drizzle } from "drizzle-orm/postgres-js";
import postgres from "postgres";

// Database connection setup
const connectionString = process.env.DATABASE_URL;
const client = postgres(connectionString!);
const db = drizzle(client);

// Storage interface with all CRUD methods
export interface IStorage {
  // User methods
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Session methods
  createSession(session: InsertSession): Promise<Session>;
  getSession(id: string): Promise<Session | undefined>;
  updateSessionPoints(id: string, totalPoints: number, hasMixedDrinks: boolean): Promise<Session | undefined>;
  getUserSessions(userId: number): Promise<Session[]>;

  // Drink methods
  addDrink(drink: InsertDrink): Promise<Drink>;
  getSessionDrinks(sessionId: string): Promise<Drink[]>;
  removeDrink(id: number): Promise<boolean>;
}

// PostgreSQL database implementation
export class DbStorage implements IStorage {
  // User methods
  async getUser(id: number): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id));
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username));
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await db.insert(users).values(insertUser).returning();
    return result[0];
  }

  // Session methods
  async createSession(session: InsertSession): Promise<Session> {
    const result = await db.insert(sessions).values(session).returning();
    return result[0];
  }

  async getSession(id: string): Promise<Session | undefined> {
    const result = await db.select().from(sessions).where(eq(sessions.id, id));
    return result[0];
  }

  async updateSessionPoints(id: string, totalPoints: number, hasMixedDrinks: boolean): Promise<Session | undefined> {
    const result = await db
      .update(sessions)
      .set({ totalPoints, hasMixedDrinks })
      .where(eq(sessions.id, id))
      .returning();
    return result[0];
  }

  async getUserSessions(userId: number): Promise<Session[]> {
    return await db
      .select()
      .from(sessions)
      .where(eq(sessions.userId, userId))
      .orderBy(sessions.createdAt);
  }

  // Drink methods
  async addDrink(drink: InsertDrink): Promise<Drink> {
    const result = await db.insert(drinks).values(drink).returning();
    return result[0];
  }

  async getSessionDrinks(sessionId: string): Promise<Drink[]> {
    return await db
      .select()
      .from(drinks)
      .where(eq(drinks.sessionId, sessionId))
      .orderBy(drinks.createdAt);
  }

  async removeDrink(id: number): Promise<boolean> {
    const result = await db
      .delete(drinks)
      .where(eq(drinks.id, id))
      .returning({ id: drinks.id });
    return result.length > 0;
  }
}

// In-memory storage implementation for fallback/testing
export class MemStorage implements IStorage {
  private usersMap: Map<number, User>;
  private sessionsMap: Map<string, Session>;
  private drinksMap: Map<number, Drink>;
  private currentUserId: number;
  private currentDrinkId: number;

  constructor() {
    this.usersMap = new Map();
    this.sessionsMap = new Map();
    this.drinksMap = new Map();
    this.currentUserId = 1;
    this.currentDrinkId = 1;
  }

  // User methods
  async getUser(id: number): Promise<User | undefined> {
    return this.usersMap.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.usersMap.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentUserId++;
    const now = new Date();
    const user: User = { ...insertUser, id, createdAt: now };
    this.usersMap.set(id, user);
    return user;
  }

  // Session methods
  async createSession(session: InsertSession): Promise<Session> {
    // Use the provided ID if available, otherwise generate one
    const id = session.id || crypto.randomUUID();
    const now = new Date();
    // Ensure all required fields have values
    const newSession: Session = { 
      ...session, 
      id, 
      createdAt: now,
      totalPoints: session.totalPoints ?? 0,
      hasMixedDrinks: session.hasMixedDrinks ?? false,
      userId: session.userId ?? null
    };
    this.sessionsMap.set(id, newSession);
    return newSession;
  }

  async getSession(id: string): Promise<Session | undefined> {
    return this.sessionsMap.get(id);
  }

  async updateSessionPoints(id: string, totalPoints: number, hasMixedDrinks: boolean): Promise<Session | undefined> {
    const session = this.sessionsMap.get(id);
    if (!session) return undefined;

    const updatedSession = { 
      ...session, 
      totalPoints, 
      hasMixedDrinks 
    };
    this.sessionsMap.set(id, updatedSession);
    return updatedSession;
  }

  async getUserSessions(userId: number): Promise<Session[]> {
    return Array.from(this.sessionsMap.values())
      .filter(session => session.userId === userId)
      .sort((a, b) => a.createdAt.getTime() - b.createdAt.getTime());
  }

  // Drink methods
  async addDrink(drink: InsertDrink): Promise<Drink> {
    const id = this.currentDrinkId++;
    const now = new Date();
    const newDrink: Drink = { 
      ...drink, 
      id, 
      createdAt: now 
    };
    this.drinksMap.set(id, newDrink);
    return newDrink;
  }

  async getSessionDrinks(sessionId: string): Promise<Drink[]> {
    return Array.from(this.drinksMap.values())
      .filter(drink => drink.sessionId === sessionId)
      .sort((a, b) => a.createdAt.getTime() - b.createdAt.getTime());
  }

  async removeDrink(id: number): Promise<boolean> {
    return this.drinksMap.delete(id);
  }
}

// Use database storage by default, with memory storage as fallback
export const storage = connectionString 
  ? new DbStorage() 
  : new MemStorage();

